import api from './api';

const handleResponse = async (promise) => {
  try {
    const response = await promise;
    console.log('API Response:', response);
    return response;
  } catch (error) {
    console.error('API Error:', {
      message: error.message,
      response: error.response?.data,
      status: error.response?.status,
      config: {
        url: error.config?.url,
        method: error.config?.method,
        data: error.config?.data
      }
    });
    throw error.response?.data?.message || error.message || 'Something went wrong';
  }
};

const API_PREFIX = '/api';

export const departmentService = {
  createDepartment: (data) => {
    console.log('Creating department with data:', data);
    return handleResponse(api.post(`${API_PREFIX}/departments`, data));
  },
  getAllDepartments: () => {
    console.log('Fetching all departments');
    return handleResponse(api.get(`${API_PREFIX}/departments`));
  },
  getDepartment: (id) => handleResponse(api.get(`${API_PREFIX}/departments/${id}`)),
  updateDepartment: (id, data) => handleResponse(api.put(`${API_PREFIX}/departments/${id}`, data)),
  deleteDepartment: (id) => handleResponse(api.delete(`${API_PREFIX}/departments/${id}`)),
};

export default departmentService;
