import api from './api';

const handleResponse = async (promise) => {
  try {
    const response = await promise;
    return response;
  } catch (error) {
    console.error('API Error:', error.response?.data || error.message);
    throw error.response?.data?.message || 'Something went wrong';
  }
};

export const departmentService = {
  createDepartment: (data) => handleResponse(api.post('/departments', data)),
  getAllDepartments: () => handleResponse(api.get('/departments')),
  getDepartment: (id) => handleResponse(api.get(`/departments/${id}`)),
  updateDepartment: (id, data) => handleResponse(api.put(`/departments/${id}`, data)),
  deleteDepartment: (id) => handleResponse(api.delete(`/departments/${id}`)),
};

export default departmentService;
