import api from './api';

const handleResponse = async (promise) => {
  try {
    const response = await promise;
    return response.data;
  } catch (error) {
    console.error('Department Service Error:', error);
    throw error;
  }
};

export const departmentService = {
  // Create a new department
  createDepartment: async (departmentData) => {
    try {
      const response = await api.post('/api/departments', departmentData);
      return response.data;
    } catch (error) {
      console.error('Create Department Error:', error);
      throw error;
    }
  },

  // Get all departments
  getAllDepartments: async () => {
    try {
      const response = await api.get('/api/departments');
      return response.data;
    } catch (error) {
      console.error('Get All Departments Error:', error);
      throw error;
    }
  },

  // Get single department by ID
  getDepartment: async (id) => {
    try {
      const response = await api.get(`/api/departments/${id}`);
      return response.data;
    } catch (error) {
      console.error(`Get Department ${id} Error:`, error);
      throw error;
    }
  },

  // Update department
  updateDepartment: async (id, departmentData) => {
    try {
      const response = await api.put(`/api/departments/${id}`, departmentData);
      return response.data;
    } catch (error) {
      console.error(`Update Department ${id} Error:`, error);
      throw error;
    }
  },

  // Delete department
  deleteDepartment: async (id) => {
    try {
      const response = await api.delete(`/api/departments/${id}`);
      return response.data;
    } catch (error) {
      console.error(`Delete Department ${id} Error:`, error);
      throw error;
    }
  }
};

export default departmentService;
