import React, { useState, useEffect } from 'react';
import departmentService from '../../services/departmentService';
import Card from '../Card';
import Modal from '../Modal';

const DepartmentManagement = () => {
  const [departments, setDepartments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    code: '',
    description: ''
  });

  useEffect(() => {
    fetchDepartments();
  }, []);

  const fetchDepartments = async () => {
    try {
      setLoading(true);
      setError('');
      const token = localStorage.getItem('token');
      console.log('Auth Token:', token ? 'Token exists' : 'No token found');

      const response = await departmentService.getAllDepartments();
      console.log('Departments response:', response);

      if (response && response.data) {
        setDepartments(response.data);
      } else {
        setError('No departments data received');
      }
    } catch (err) {
      console.error('Error fetching departments:', err);
      setError(err.message || 'Failed to fetch departments. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      setError('');
      setSuccess('');

      // Validate required fields
      if (!formData.name || !formData.code) {
        setError('Name and Code are required');
        return;
      }

      console.log('Submitting department data:', formData);

      // Get the current token for debugging
      const token = localStorage.getItem('token');
      console.log('Current auth token:', token ? 'Token exists' : 'No token found');

      try {
        const response = await departmentService.createDepartment(formData);
        console.log('Create department response:', response);

        if (response && response.data) {
          setSuccess('Department created successfully');
          setFormData({ name: '', code: '', description: '' });
          setShowModal(false);

          // Refresh the departments list after a short delay
          setTimeout(() => {
            fetchDepartments();
          }, 500);
        } else {
          throw new Error('Invalid response from server');
        }
      } catch (apiError) {
        console.error('API Error details:', {
          message: apiError.message,
          response: apiError.response?.data,
          status: apiError.response?.status,
          headers: apiError.response?.headers
        });
        throw apiError; // Re-throw to be caught by the outer catch
      }

    } catch (err) {
      console.error('Error in handleSubmit:', {
        error: err,
        message: err.message,
        response: err.response?.data
      });

      let errorMessage = 'Failed to create department. Please try again.';

      // More specific error messages based on the error
      if (err.response) {
        // The request was made and the server responded with a status code
        // that falls out of the range of 2xx
        if (err.response.status === 401) {
          errorMessage = 'Your session has expired. Please log in again.';
        } else if (err.response.status === 403) {
          errorMessage = 'You do not have permission to create departments.';
        } else if (err.response.data && err.response.data.message) {
          errorMessage = err.response.data.message;
        }
      } else if (err.request) {
        // The request was made but no response was received
        errorMessage = 'No response from server. Please check your connection.';
      }

      setError(errorMessage);
    }
  };

  const handleDeleteDepartment = async (id) => {
    if (!window.confirm('Are you sure you want to delete this department? This action cannot be undone.')) {
      return;
    }

    try {
      setError('');
      setSuccess('');

      const response = await departmentService.deleteDepartment(id);
      console.log('Delete department response:', response);

      setSuccess('Department deleted successfully');

      // Refresh the departments list after a short delay
      setTimeout(() => {
        fetchDepartments();
      }, 500);

    } catch (err) {
      console.error('Error deleting department:', err);
      setError(err.message || 'Failed to delete department. Please try again.');
    }
  };

  if (loading) return <div className="loading">Loading departments...</div>;

  return (
    <div>
      <Card title="Department Management">
        {error && <div className="error-message">{error}</div>}
        {success && <div className="success-message">{success}</div>}

        <button 
          onClick={() => setShowModal(true)}
          className="btn btn-primary"
          style={{ marginBottom: '20px' }}
        >
          + Add Department
        </button>

        {departments.length === 0 ? (
          <p>No departments found</p>
        ) : (
          <div className="table-container">
            <table>
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Code</th>
                  <th>Description</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {departments.map((dept) => (
                  <tr key={dept._id}>
                    <td>{dept.name}</td>
                    <td>{dept.code}</td>
                    <td>{dept.description}</td>
                    <td>
                      <button 
                        onClick={() => handleDeleteDepartment(dept._id)}
                        className="btn btn-danger btn-small"
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </Card>

      <Modal isOpen={showModal} onClose={() => setShowModal(false)} title="Add Department">
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Department Name</label>
            <input
              type="text"
              value={formData.name}
              onChange={(e) => setFormData({...formData, name: e.target.value})}
              required
            />
          </div>
          <div className="form-group">
            <label>Code</label>
            <input
              type="text"
              value={formData.code}
              onChange={(e) => setFormData({...formData, code: e.target.value})}
              required
            />
          </div>
          <div className="form-group">
            <label>Description</label>
            <textarea
              value={formData.description}
              onChange={(e) => setFormData({...formData, description: e.target.value})}
            />
          </div>
          <div className="form-buttons">
            <button type="submit" className="btn btn-success">Create</button>
            <button type="button" onClick={() => setShowModal(false)} className="btn btn-secondary">Cancel</button>
          </div>
        </form>
      </Modal>
    </div>
  );
};

export default DepartmentManagement;
