import React, { useState, useEffect, useContext } from 'react';
import { AuthContext } from '../../context/AuthContext';
import Card from '../Card';

const FacultyHome = () => {
  const { user } = useContext(AuthContext);

  return (
    <div>
      <h2 className="section-title">Faculty Dashboard</h2>
      
      <Card title="Welcome">
        <p>Hello {user?.name}, you can manage attendance, marks, and handle leave requests from this dashboard.</p>
      </Card>

      <Card title="Quick Links">
        <ul>
          <li>✓ Mark attendance for your students</li>
          <li>✓ Enter and update marks</li>
          <li>✓ Request leaves</li>
          <li>✓ View announcements</li>
        </ul>
      </Card>
    </div>
  );
};

export default FacultyHome;
