import React, { useState, useEffect } from 'react';
import departmentService from '../../services/departmentService';
import Card from '../Card';
import Modal from '../Modal';
import './DepartmentManagement.css';

const DepartmentManagement = () => {
  const [departments, setDepartments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    code: '',
    description: ''
  });
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Fetch departments on component mount
  useEffect(() => {
    fetchDepartments();
  }, []);

  // Fetch all departments
  const fetchDepartments = async () => {
    try {
      setLoading(true);
      setError('');

      const departments = await departmentService.getAllDepartments();
      setDepartments(departments || []);

    } catch (error) {
      console.error('Error fetching departments:', error);
      setError(error.message || 'Failed to load departments. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Basic validation
    if (!formData.name.trim() || !formData.code.trim()) {
      setError('Name and Code are required fields');
      return;
    }

    // Validate code format (uppercase letters and numbers only)
    const codeRegex = /^[A-Z0-9]+$/;
    if (!codeRegex.test(formData.code)) {
      setError('Code must contain only uppercase letters and numbers');
      return;
    }

    try {
      setIsSubmitting(true);
      setError('');
      setSuccess('');

      // Prepare department data
      const departmentData = {
        name: formData.name.trim(),
        code: formData.code.trim().toUpperCase(),
        description: formData.description?.trim() || ''
      };

      console.log('Creating department with data:', departmentData);

      const createdDept = await departmentService.createDepartment(departmentData);
      console.log('Department created:', createdDept);

      // Reset form and show success message
      setFormData({ name: '', code: '', description: '' });
      setSuccess('Department created successfully!');
      setShowModal(false);

      // Refresh the departments list after a short delay
      setTimeout(() => {
        fetchDepartments();
      }, 500);

    } catch (error) {
      console.error('Error creating department:', error);
      const errorMessage = error.response?.data?.error || error.message || 'Failed to create department. Please try again.';
      setError(errorMessage);
    } finally {
      setIsSubmitting(false);
    }
  };

  // Handle department deletion
  const handleDeleteDepartment = async (id) => {
    if (!window.confirm('Are you sure you want to delete this department? This action cannot be undone.')) {
      return;
    }

    try {
      setError('');
      await departmentService.deleteDepartment(id);
      setSuccess('Department deleted successfully!');

      // Refresh the departments list
      fetchDepartments();

    } catch (error) {
      console.error('Error deleting department:', error);
      setError(error.message || 'Failed to delete department. Please try again.');
    }
  };

  // Loading state
  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>Loading departments...</p>
      </div>
    );
  }

  return (
    <div className="department-management">
      <Card title="Department Management">
        <div className="department-actions">
          <button 
            onClick={() => setShowModal(true)}
            className="btn btn-primary"
            disabled={isSubmitting}
          >
            {isSubmitting ? 'Processing...' : '+ Add Department'}
          </button>

          {error && <div className="alert alert-danger">{error}</div>}
          {success && <div className="alert alert-success">{success}</div>}
        </div>

        {departments.length === 0 ? (
          <div className="no-departments">
            <p>No departments found. Click the button above to add one.</p>
          </div>
        ) : (
          <div className="table-responsive">
            <table className="department-table">
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Code</th>
                  <th>Description</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {departments.map((dept) => (
                  <tr key={dept._id}>
                    <td>{dept.name}</td>
                    <td className="code-cell">{dept.code}</td>
                    <td>{dept.description || '-'}</td>
                    <td className="actions-cell">
                      <button 
                        onClick={() => handleDeleteDepartment(dept._id)}
                        className="btn btn-danger btn-sm"
                        disabled={isSubmitting}
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </Card>

      {/* Add Department Modal */}
      <Modal 
        isOpen={showModal} 
        onClose={() => !isSubmitting && setShowModal(false)}
        title="Add New Department"
      >
        <form onSubmit={handleSubmit}>
          {error && <div className="alert alert-danger">{error}</div>}
          <div className="form-group mb-3">
            <label htmlFor="name" className="form-label">Department Name *</label>
            <input
              type="text"
              id="name"
              name="name"
              className="form-control"
              value={formData.name}
              onChange={handleInputChange}
              placeholder="e.g., Computer Science"
              required
              disabled={isSubmitting}
            />
          </div>

          <div className="form-group mb-3">
            <label htmlFor="code" className="form-label">Department Code *</label>
            <input
              type="text"
              id="code"
              name="code"
              className="form-control"
              value={formData.code}
              onChange={handleInputChange}
              placeholder="e.g., CS"
              required
              disabled={isSubmitting}
              style={{ textTransform: 'uppercase' }}
            />
            <div className="form-text">Use uppercase letters and numbers only (e.g., CS, IT, MECH)</div>
          </div>

          <div className="form-group mb-4">
            <label htmlFor="description" className="form-label">Description</label>
            <textarea
              id="description"
              name="description"
              className="form-control"
              value={formData.description}
              onChange={handleInputChange}
              placeholder="Optional department description"
              rows="3"
              disabled={isSubmitting}
            ></textarea>
          </div>

          <div className="d-flex justify-content-end gap-2 mt-4">
            <button 
              type="button" 
              className="btn btn-secondary"
              onClick={() => setShowModal(false)}
              disabled={isSubmitting}
            >
              Cancel
            </button>
            <button
              type="submit"
              className="btn btn-primary"
              disabled={isSubmitting || !formData.name.trim() || !formData.code.trim()}
            >
              {isSubmitting ? (
                <>
                  <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                  Creating...
                </>
              ) : 'Create Department'}
            </button>
          </div>
        </form>
      </Modal>
    </div>
  );
};

export default DepartmentManagement;
