import React, { useState, useEffect } from 'react';
import { FiUsers, FiBook, FiCalendar, FiAlertCircle } from 'react-icons/fi';
import userService from '../../services/userService';
import departmentService from '../../services/departmentService';
import leaveService from '../../services/leaveService';
import Card from '../Card';
//HEELOO
const AdminHome = () => {
  const [stats, setStats] = useState({
    users: 0,
    departments: 0,
    pendingLeaves: 0
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchStats();
  }, []);

  const fetchStats = async () => {
    try {
      setLoading(true);

      // Fetch all data in parallel
      const [usersResponse, deptsResponse, leavesResponse] = await Promise.all([
        userService.getAllUsers().then(res => res.data || []).catch(() => []),
        departmentService.getAllDepartments().then(res => Array.isArray(res) ? res : []).catch(() => []),
        leaveService.getLeaves ? leaveService.getLeaves({ status: 'pending' }).then(res => res?.data || []).catch(() => []) : Promise.resolve([])
      ]);

      console.log('Fetched stats:', {
        users: usersResponse,
        departments: deptsResponse,
        pendingLeaves: leavesResponse
      });

      setStats({
        users: Array.isArray(usersResponse) ? usersResponse.length : 0,
        departments: Array.isArray(deptsResponse) ? deptsResponse.length : 0,
        pendingLeaves: Array.isArray(leavesResponse) ? leavesResponse.length : 0
      });

    } catch (error) {
      console.error('Error fetching dashboard stats:', {
        error: error,
        message: error.message,
        response: error.response?.data
      });
      // Set default values in case of error
      setStats({
        users: 0,
        departments: 0,
        pendingLeaves: 0
      });
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  return (
    <div>
      <h2 className="section-title">Dashboard Overview</h2>

      <div className="stats-grid">
        <Card className="stat-card">
          <div className="stat-icon"><FiUsers /></div>
          <div className="stat-content">
            <h3>Total Users</h3>
            <div className="stat-number">{stats.users}</div>
          </div>
        </Card>

        <Card className="stat-card">
          <div className="stat-icon"><FiBook /></div>
          <div className="stat-content">
            <h3>Departments</h3>
            <div className="stat-number">{stats.departments}</div>
          </div>
        </Card>

        <Card className="stat-card">
          <div className="stat-icon"><FiCalendar /></div>
          <div className="stat-content">
            <h3>Pending Leaves</h3>
            <div className="stat-number">{stats.pendingLeaves}</div>
          </div>
        </Card>
      </div>

      <Card title="Quick Actions">
        <p>Use the menu on the left to manage users, departments, and leave requests.</p>
      </Card>
    </div>
  );
};

export default AdminHome;
