import React, { useState, useContext, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import './Login.css';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const { login, loading, error: authError } = useContext(AuthContext);
  const navigate = useNavigate();

  // Update local error state when auth context error changes
  useEffect(() => {
    if (authError) {
      setError(authError);
    }
  }, [authError]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    // Basic validation
    if (!email.trim() || !password) {
      setError('Please enter both email and password');
      return;
    }

    // Email format validation
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(email)) {
      setError('Please enter a valid email address');
      return;
    }

    try {
      const response = await login(email, password);

      // Redirect based on user role
      if (response?.user?.role) {
        switch(response.user.role.toLowerCase()) {
          case 'admin':
            navigate('/admin');
            break;
          case 'faculty':
            navigate('/faculty');
            break;
          case 'student':
            navigate('/student');
            break;
          default:
            navigate('/');
        }
      } else {
        // If no role is defined, redirect to home
        navigate('/');
      }
    } catch (err) {
      // Error is already handled in the AuthContext
      console.error('Login error in component:', err);
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <div className="login-header">
          <h1>College ERP System</h1>
          <p>Integrated Management Solution</p>
        </div>

        <form onSubmit={handleSubmit} className="login-form">
          {error && (
            <div className="alert alert-error">
              <i className="fas fa-exclamation-circle"></i>
              <span>{error}</span>
            </div>
          )}

          <div className="form-group">
            <label htmlFor="username">Email Address</label>
            <div className="input-with-icon">
              <i className="fas fa-envelope"></i>
              <input
                type="email"
                id="username"
                value={username}
                onChange={(e) => setUsername(e.target.value.trim())}
                required
                placeholder="Enter your email address"
                autoComplete="username"
                disabled={loading}
              />
            </div>
          </div>

          <div className="form-group">
            <div className="password-header">
              <label htmlFor="password">Password</label>
              <button 
                type="button" 
                className="forgot-password"
                onClick={() => alert('Please contact administrator to reset your password.')}
              >
                Forgot Password?
              </button>
            </div>
            <div className="input-with-icon">
              <i className="fas fa-lock"></i>
              <input
                type={showPassword ? 'text' : 'password'}
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
                placeholder="Enter your password"
                autoComplete="current-password"
                disabled={loading}
              />
              <button 
                type="button" 
                className="toggle-password"
                onClick={() => setShowPassword(!showPassword)}
                tabIndex="-1"
              >
                <i className={`fas fa-eye${showPassword ? '-slash' : ''}`}></i>
              </button>
            </div>
          </div>

          <button 
            type="submit" 
            className={`login-btn ${loading ? 'loading' : ''}`}
            disabled={loading}
          >
            {loading ? (
              <>
                <span className="spinner"></span>
                <span>Logging in...</span>
              </>
            ) : 'Login'}
          </button>
        </form>

        <div className="demo-credentials">
          <h3>Demo Credentials</h3>
          <div className="credential">
            <span className="role">Admin:</span>
            <span>admin@college.com / admin123</span>
          </div>
          <div className="credential">
            <span className="role">Faculty:</span>
            <span>faculty@college.com / faculty123</span>
          </div>
          <div className="credential">
            <span className="role">Student:</span>
            <span>student@college.com / student123</span>
          </div>
        </div>

        <div className="login-footer">
          <p>Â© {new Date().getFullYear()} College ERP. All rights reserved.</p>
          <p>Need help? <a href="mailto:support@collegeerp.com">Contact Support</a></p>
        </div>
      </div>
    </div>
  );
};

export default Login;
