import React, { useState, useEffect } from 'react';
import userService from '../../services/userService';
import departmentService from '../../services/departmentService';
import Card from '../Card';
import Modal from '../Modal';

const UserManagement = () => {
  const [users, setUsers] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    password: '',
    role: 'student',
    department: '',
    enrollmentNumber: '',
    employeeId: '',
    semester: ''
  });

  useEffect(() => {
    fetchUsers();
    fetchDepartments();
  }, []);

  const fetchUsers = async () => {
    try {
      setLoading(true);
      const response = await userService.getAllUsers();
      setUsers(response.data);
    } catch (err) {
      setError('Failed to fetch users');
    } finally {
      setLoading(false);
    }
  };

  const fetchDepartments = async () => {
    try {
      const response = await departmentService.getAllDepartments();
      setDepartments(response.data);
    } catch (err) {
      console.error('Failed to fetch departments:', err);
    }
  };

  const handleDeleteUser = async (id) => {
    if (window.confirm('Are you sure you want to delete this user?')) {
      try {
        await userService.deleteUser(id);
        setSuccess('User deleted successfully');
        fetchUsers();
      } catch (err) {
        setError('Failed to delete user');
      }
    }
  };

  const handleStatusChange = async (id, newStatus) => {
    try {
      await userService.updateUserStatus(id, newStatus);
      setSuccess('User status updated');
      fetchUsers();
    } catch (err) {
      setError('Failed to update user status');
    }
  };

  const handleCreateUser = async (e) => {
    e.preventDefault();
    try {
      setError('');
      setSuccess('');

      const userData = {
        name: formData.name,
        password: formData.password,
        role: formData.role,
        department: formData.department,
        enrollmentNumber: formData.role === 'student' ? formData.enrollmentNumber : undefined,
        employeeId: formData.role === 'faculty' ? formData.employeeId : undefined,
        semester: formData.role === 'student' ? parseInt(formData.semester) : undefined
      };

      await userService.createUser(userData);
      setSuccess('User created successfully');
      setShowModal(false);
      setFormData({
        name: '',
        password: '',
        role: 'student',
        department: '',
        enrollmentNumber: '',
        employeeId: '',
        semester: ''
      });
      fetchUsers();
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to create user');
    }
  };

  const handleRoleChange = (role) => {
    setFormData({
      ...formData,
      role,
      enrollmentNumber: role === 'student' ? formData.enrollmentNumber : '',
      employeeId: role === 'faculty' ? formData.employeeId : '',
      semester: role === 'student' ? formData.semester : ''
    });
  };

  if (loading) return <div className="loading">Loading users...</div>;

  return (
    <div>
      <Card title="User Management">
        {error && <div className="error-message">{error}</div>}
        {success && <div className="success-message">{success}</div>}

        {users.length === 0 ? (
          <p>No users found</p>
        ) : (
          <div className="table-container">
            <table>
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Email</th>
                  <th>Role</th>
                  <th>Status</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {users.map((user) => (
                  <tr key={user._id}>
                    <td>{user.name}</td>
                    <td>{user.email}</td>
                    <td>{user.role}</td>
                    <td>
                      <select 
                        value={user.status} 
                        onChange={(e) => handleStatusChange(user._id, e.target.value)}
                      >
                        <option value="active">Active</option>
                        <option value="inactive">Inactive</option>
                      </select>
                    </td>
                    <td>
                      <button 
                        onClick={() => handleDeleteUser(user._id)}
                        className="btn btn-danger btn-small"
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </Card>
    </div>
  );
};

export default UserManagement;
