import React, { useState, useContext } from 'react';
import { AuthContext } from '../../context/AuthContext';
import attendanceService from '../../services/attendanceService';
import Card from '../Card';

const AttendanceManagement = () => {
  const { user } = useContext(AuthContext);
  const [attendanceRecords, setAttendanceRecords] = useState([]);
  const [date, setDate] = useState(new Date().toISOString().split('T')[0]);
  const [courseId, setCourseId] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleAddRow = () => {
    setAttendanceRecords([
      ...attendanceRecords,
      { studentId: '', status: 'present', remarks: '' }
    ]);
  };

  const handleUpdateRow = (index, field, value) => {
    const newRecords = [...attendanceRecords];
    newRecords[index][field] = value;
    setAttendanceRecords(newRecords);
  };

  const handleRemoveRow = (index) => {
    setAttendanceRecords(attendanceRecords.filter((_, i) => i !== index));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const records = attendanceRecords.map(record => ({
        course: courseId,
        student: record.studentId,
        faculty: user?.id,
        date,
        status: record.status,
        remarks: record.remarks
      }));

      await attendanceService.bulkCreateAttendance({ attendanceRecords: records });
      setSuccess('Attendance marked successfully');
      setAttendanceRecords([]);
    } catch (err) {
      setError('Failed to mark attendance');
    }
  };

  return (
    <div>
      <Card title="Mark Attendance">
        {error && <div className="error-message">{error}</div>}
        {success && <div className="success-message">{success}</div>}

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Date</label>
            <input
              type="date"
              value={date}
              onChange={(e) => setDate(e.target.value)}
              required
            />
          </div>

          <div className="form-group">
            <label>Course ID</label>
            <input
              type="text"
              value={courseId}
              onChange={(e) => setCourseId(e.target.value)}
              required
            />
          </div>

          <button type="button" onClick={handleAddRow} className="btn btn-primary">
            + Add Student
          </button>

          {attendanceRecords.length > 0 && (
            <div className="table-container" style={{ marginTop: '20px' }}>
              <table>
                <thead>
                  <tr>
                    <th>Student ID</th>
                    <th>Status</th>
                    <th>Remarks</th>
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {attendanceRecords.map((record, index) => (
                    <tr key={index}>
                      <td>
                        <input
                          type="text"
                          value={record.studentId}
                          onChange={(e) => handleUpdateRow(index, 'studentId', e.target.value)}
                          required
                        />
                      </td>
                      <td>
                        <select
                          value={record.status}
                          onChange={(e) => handleUpdateRow(index, 'status', e.target.value)}
                        >
                          <option value="present">Present</option>
                          <option value="absent">Absent</option>
                          <option value="late">Late</option>
                        </select>
                      </td>
                      <td>
                        <input
                          type="text"
                          value={record.remarks}
                          onChange={(e) => handleUpdateRow(index, 'remarks', e.target.value)}
                        />
                      </td>
                      <td>
                        <button 
                          type="button"
                          onClick={() => handleRemoveRow(index)}
                          className="btn btn-danger btn-small"
                        >
                          Remove
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}

          <div className="form-buttons" style={{ marginTop: '20px' }}>
            <button type="submit" className="btn btn-success">Submit Attendance</button>
          </div>
        </form>
      </Card>
    </div>
  );
};

export default AttendanceManagement;
