import api from './api';

export const attendanceService = {
  createAttendance: (data) => api.post('/attendance', data),
  bulkCreateAttendance: (data) => api.post('/attendance/bulk', data),
  getAllAttendance: () => api.get('/attendance'),
  getStudentAttendance: (studentId) => api.get(`/attendance/student/${studentId}`),
  getCourseAttendance: (courseId) => api.get(`/attendance/course/${courseId}`),
  updateAttendance: (id, data) => api.put(`/attendance/${id}`, data),
  deleteAttendance: (id) => api.delete(`/attendance/${id}`),
};

export default attendanceService;
